exports=async function(url,bid,cid,options){const{parseContent,trimBeforeContent}=await require("transcode/parse_content");return parseContent(url,"div#content",{clientOptions:{...(options&&options.clientOptions)},preprocess:html=>html.replace(/id="content"[^>]*>\s*<p>[\S\s]+?<\/p>/,"id=\"content\">")})};