exports=async function(url,bid,cid,options){const{parseContent,trimContentFragment,buildImg}=await require("transcode/parse_content");return parseContent(url,{cleaner:html=>trimContentFragment(html,/class="article-text"[^>]*>/)},{clientOptions:{...(options&&options.clientOptions)},parser:html=>{let fragment=trimContentFragment(html,/class="article-text"[^>]*>/);fragment=fragment.toIndexOf("</div>");const lines=fragment.subRegexAll(/<p[^>]+>(?:((?:[^<]|<[^\/])+)|<a[^>]+>\s*<img [^>]*(src=['"][^'"]+)['"][^>]*>\s*<\/a>)<\/p>/g);return lines.map(_=>_.startsWith("src=")?buildImg(_.substring(5).trim()):_)}})};