exports=async function(url,bid,cid,options){const{parseContent}=await require("transcode/parse_content");return parseContent(url,null,{clientOptions:{...(options&&options.clientOptions)},parser:html=>{const fragment=html.subRegex(/id="content"[^>]*>(?<fragment>(?:[^<]|<br\s*\/?>|<p[^>]*>|<\/p>)*)</),result=[];for(let m of fragment.matchAll(/<p class="content_detail">(?<p>[^<]+)</g))m&&m.groups.p&&result.push(m.groups.p.replace(/记住网址.+/,""));return result}})};