exports=async function(bid,options){const{parseCatalogPaginate,getSelectLastOption}=await require("transcode/parse_catalog"),clientOptions={...(options&&options.clientOptions)};return parseCatalogPaginate(page=>`https://www.mayi89.com/txt/${bid}${1===page?"":"/"+page}.html`,".section-list",{...options,clientOptions,pageSize:50,totalPagesRegex:html=>{const opt=getSelectLastOption(html,s=>-1!==s.indexOf("\u7AE0"));return opt?parseInt(opt.subRegex(/\/txt\/\d+\/(\d+)\.html/)):null}})};