exports=async function(url,bid,cid,options){const{parseContent}=await require("transcode/parse_content");return parseContent(url,"div.content",{clientOptions:{...(options&&options.clientOptions)},transformer:f=>f.replace(/^\s*<p class="contnew"[^>]*>[^<]+<\/p>[^<]*一秒记住[^<]+/,"").replace(/<p class="contnew">[^<]+<\/p>\s*<p>为了方便下次[^<]+<\/p>\s*$/,"")})};