exports=async function(bid,options){const{parseUpdate}=await require("transcode/parse_update");return parseUpdate(`http://www.mianfeixiaoshuoyueduwang.com/book/${bid}/`,html=>({name:html.subRegexLast(/<li[^>]*>\s*<a[^>]+>(?:\s*<span[^>]*>)?([^<]+)</g)}),{clientOptions:{...(options&&options.clientOptions),"x-mobile":0}})};