exports=async function(url,bid,cid,options){const{parseContentPaginate,getLinkElement}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page}.html`),"#chaptercontent",{clientOptions:{...(options&&options.clientOptions),"x-mobile":1},hasNextFn:html=>null!=/\/\d+_\d+\.html/.exec(getLinkElement(html,"id=\"pb_next\"")),transformer:f=>f.replace(/<p[^>]*>(?:[^<]|<\/?a[^>]*>)+<\/p>/g,"")})};