exports=async function(bid,options){const{parseCatalogPaginate,selectIndexTotalPagesRegex}=await require("transcode/parse_catalog"),clientOptions={...(options&&options.clientOptions),encoding:"gbk"};return parseCatalogPaginate(page=>`http://www.mlxs.org/book/${bid}/${1===page?"":"index_"+page+".html"}`,"dl.panel-chapterlist",{...options,clientOptions,pageSize:60,totalPagesRegex:selectIndexTotalPagesRegex})};