exports=async function(url,bid,cid,options){const{parseContent}=await require("transcode/parse_content"),replacePinYin=await require("transcode/pinyin_map");return parseContent(url,"div.content",{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"},preprocess:html=>replacePinYin(html,/<img src="mom\/([a-z\d]+)\.jpg">/g)})};