exports=async function(url,bid,cid,options){const{parseContentPaginate,getLinkElement}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page}.html`),"div#htmlContent",{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"},hasNextFn:html=>null!=/\d+_\d+\.html/.exec(getLinkElement(html,"id=\"linkNext\"")),transformer:f=>f.replace(/-->>\s*<p[^>]+text-danger[^>]+>[^<]*<\/p>/,"").replace(/【[^】]+\w+\.\w+\.\w+】/g,"")})};