exports=async function(url,bid,cid,options){const{parseContent,trimBeforeContent}=await require("transcode/parse_content");return parseContent(url,null,{clientOptions:{...(options&&options.clientOptions)},parser:html=>{const index=html.indexOf("id=\"txt\">");if(-1===index)return null;const fragment=html.substring(index);return fragment.subRegexMapAll(/<dd data-id="(?<order>\d+)">(?<content>.+?)<\/dd>/g).map(_=>({order:parseInt(_.order),content:_.content})).sort((a,b)=>a.order-b.order).filter(_=>999!==_.order).map(_=>_.content.split(/<\/?p>/).map(_=>_.trim())).flat().filter(_=>0<_.length)}})};