exports=async function(bid,options){const{parseCatalogPaginate,cleanCatalogWrapper}=await require("transcode/parse_catalog"),clientOptions={...(options&&options.clientOptions),encoding:"gbk"};return parseCatalogPaginate(page=>`https://www.plxs.org/book/${bid}/${1===page?"":"index_"+page+".html"}`,"dl.panel-chapterlist",{...options,clientOptions,pageSize:60,totalPagesRegex:/value="\/book\/\d+\/index_(?<totalPages>\d+)\.html"[^>]*>[^<]+<\/option>\s*<\/select/})};