exports=async function(bid,options){const{parseInfo}=await require("transcode/parse_info"),url=`https://www.qududu.net/book/${bid.idPrefix()}/${bid}/`;return parseInfo("\u53BB\u8BFB\u8BFB",url,"meta",{clientOptions:{...(options&&options.clientOptions)}}).then(_=>(_.intro=_.intro.replace(/^\s*《[^》]+》/,"").trim(),_))};