exports=async function(url,bid,cid,options){const{parseContent,trimContentFragment}=await require("transcode/parse_content");return parseContent(url,{cleaner:html=>{const f=trimContentFragment(html,/class="content"[^>]*>/),m=/^(?:\s|&nbsp;)*<p[^>]*>/.exec(f);return m?f.substring(m[0].length):f}},{clientOptions:{...(options&&options.clientOptions)}})};