exports=async function(bid,options){const{parseCatalogPaginate,getSelectLastOption}=await require("transcode/parse_catalog"),replaceFont=await require("transcode/font_map"),clientOptions={...(options&&options.clientOptions)};return parseCatalogPaginate(page=>`http://www.shdataic.com/${bid}/${page}/`,"dl.panel-chapterlist",{...options,clientOptions,pageSize:100,totalPagesRegex:html=>getSelectLastOption(html,s=>-1!==s.indexOf("\u7AE0"))?.subRegex(/\/(\d+)\/?"/)?.intValue(),preprocess:html=>replaceFont(html,/<i>&#x([a-f\d]{4});?<\/i>/g),idRegex:/\/(?<id>[a-z\d]+)\.html/})};