exports=async function(url,bid,cid,options){const{parseContent}=await require("transcode/parse_content"),replaceFont=await require("transcode/font_map");return parseContent(url,"#chaptercontent",{clientOptions:{...(options&&options.clientOptions)},preprocess:html=>replaceFont(html,/<i>&#x([a-f\d]{4});?<\/i>/g),transformer:f=>f.replace(/^[\s　]*<p>[^<]+提示您：看后[^<]+<\/p>/,"")})};