exports=async function(url,bid,cid,options){const{parseContentPaginate,trimBeforeContent}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page}.html`),"div#content",{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"},hasNextFn:html=>null!=/<a href="\/read\/\d+\/\d+_\d+\.html">下一页</.exec(html)})};