exports=async function(bid,options){const{parseCatalogPaginate,getSelectLastOption}=await require("transcode/parse_catalog"),clientOptions={...(options&&options.clientOptions),encoding:"gbk"};return parseCatalogPaginate(page=>`http://www.shukeju.net/book/${bid}/${1===page?"":"index_"+page+".html"}`,"ul.chapterlist",{...options,clientOptions,pageSize:60,totalPagesRegex:html=>getSelectLastOption(html,s=>-1!==s.indexOf("value=\"/book/"))?.subRegex(/\/index_(\d+)\.html/)?.intValue()})};