exports=async function(url,bid,cid,options){const{parseContentPaginate,hasMorePageCleaner,cleanContentRemember}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page}.html`),"div#content",{clientOptions:{...(options&&options.clientOptions)},hasNextFn:html=>null!=/\/\d+_\d+\.html"[^>]*>\s*下一页\s*<\/a>/.exec(html),pageCleaner:hasMorePageCleaner,merger:null}).then(cleanContentRemember)};