exports=async function(url,bid,cid,options){const{parseContentPaginate,hasMorePageCleaner,getLinkElement,cleanContentRemember}=await require("transcode/parse_content"),clientOptions={...(options&&options.clientOptions),followCookies:!0};return parseContentPaginate(page=>1===page?url:url.replace(".html","_"+page+".html"),"div#content",{...options,clientOptions,hasNextFn:html=>/\/\d+\/\d+_\d+\.html/.test(getLinkElement(html,"class=\"next\"")),pageCleaner:hasMorePageCleaner}).then(cleanContentRemember)};