exports=async function(bid,options){const{send}=require("http"),response=await send({url:`https://xs.taozmh.com/info/chapters/${bid}`,headers:{referer:"https://xs.taozmh.com/"}},{...(options&&options.clientOptions),"x-mobile":1});assertOK(response);const html=response.body,json=JSON.parse(html);return formatCatalog(json.chapters.map(_=>({id:_.id.toString(),name:_.title,url:`https://xs.taozmh.com/#/read/${bid}`})))};