exports=async function(url,bid,cid,options){const{send}=require("http"),response=await send({url:`https://xs.taozmh.com/info/content/${cid}`,headers:{referer:"https://xs.taozmh.com/"}},{...(options&&options.clientOptions),"x-mobile":1});assertOK(response);const html=response.body,json=JSON.parse(html);return formatBookContent(json.chapter.content.split("\n").map(_=>_.trim()).filter(_=>0<_.length))};