exports=async function(bid,options){const{send}=require("http"),response=await send({url:`https://xs.taozmh.com/info/book/${bid}`,headers:{referer:"https://xs.taozmh.com/"}},{...(options&&options.clientOptions),"x-mobile":1});assertOK(response);const html=response.body,json=JSON.parse(html);return formatUpdate({url:`https://xs.taozmh.com/#/book/${bid}`,name:json.lastChapter,updateTime:parseDate(json.create_date)})};