exports=async function(url,bid,cid,options){const{parseContent}=await require("transcode/parse_content"),{redirectCookiesRetry}=await require("transcode/utils");return parseContent(url,"div#htmlContent",{httpOptions:{followRedirects:!1},clientOptions:{...(options&&options.clientOptions),encoding:"gbk"},retry:redirectCookiesRetry,preprocess:html=>html.replace(/<p class="booktag">([^<]+|<\/?b>)+<\/p>/g,"")})};