exports=async function(url,bid,cid,options){const{parseContent,trimContentFragment}=await require("transcode/parse_content");return parseContent(url,{cleaner:html=>trimContentFragment(html,/\/h1>(?:\s*<p align="center">.*?<\/p>)?\s*<p>/,"p")},{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"}})};