exports=async function(bid,options){const url=`https://cn.ttkan.co/novel/chapters/${bid}`,{send}=require("http"),URL=require("url"),uri=new URL(url),response=await send({url:uri.resolve(`/api/nq/amp_novel_chapters?language=cn&novel_id=${bid}&__amp_source_origin=${encodeURIComponent(uri.origin)}`),headers:{referer:url}},options?.clientOptions);assertOK(response);const html=response.body,json=JSON.parse(html);return json.items.map(_=>({id:_.chapter_id+"",name:_.chapter_name.trim(),url:uri.resolve(`/novel/user/page_direct?novel_id=${bid}&page=${_.chapter_id}`)}))};