exports=async function(bid,options){const{parseCatalogPaginate,getSelectLastOption}=await require("transcode/parse_catalog"),clientOptions={...(options&&options.clientOptions)};return parseCatalogPaginate(page=>`https://www.ttzw.com/book/${bid}/${1===page?"":"index_"+page+".html"}`,".section-list",{...options,clientOptions,pageSize:20,totalPagesRegex:html=>getSelectLastOption(html,s=>-1!==s.indexOf("\u7AE0"))?.subRegex(/\/index_(\d+)\.html/)?.intValue()})};