exports=async function(url,bid,cid,options){const{parseContent,trimContentFragment}=await require("transcode/parse_content");return parseContent(url,{cleaner:html=>trimContentFragment(html,/id="TextContent"[^>]*>/)},{clientOptions:{...(options&&options.clientOptions)},preprocess:html=>html.replace(/<p><a[^>]+>[^<]+<\/a><\/p>/g,"")})};