exports=async function(bid,options){const{parseCatalogPaginate,cleanCatalogWrapper,hrefRecordRegex,getSelectLastOption}=await require("transcode/parse_catalog"),clientOptions={...(options&&options.clientOptions),encoding:"gbk"};return parseCatalogPaginate(page=>`https://www.wx84.net/book/${bid}/${1===page?"":"index_"+page+".html"}`,{cleaner:html=>cleanCatalogWrapper(html,/class="mulu_list"/,"</ul>"),recordRegex:hrefRecordRegex},{...options,clientOptions,pageSize:60,totalPagesRegex:html=>getSelectLastOption(html,s=>/第\d+页/.test(s))?.subRegex(/index_(\d+)\.html/)?.intValue()})};