exports=async function(url,bid,cid,options){const{parseContent}=await require("transcode/parse_content");return parseContent(url,"div#content",{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"},preprocess:html=>html.replace(/一秒[記记]住[^<]+<a [^>]+>[^<]+<\/a>[^<]+/,"").replace(/手机阅读[^<]+精彩小[說说][閱阅][讀读][^<]+/,"")})};