exports=async function(bid,options){const{parseCatalogPaginate,getSelectLastOption}=await require("transcode/parse_catalog"),replaceFont=await require("transcode/font_map"),clientOptions={...(options&&options.clientOptions)};return parseCatalogPaginate(page=>`https://www.xiaoshuomm.com/books/${bid.idPrefix()}/${bid}/${page}/`,"dl.panel-chapterlist",{...options,clientOptions,pageSize:40,totalPagesRegex:html=>getSelectLastOption(html,s=>-1!==s.indexOf("\u7AE0"))?.subRegex(/\/books\/\d+\/\d+\/(?<totalPages>\d+)/)?.intValue(),preprocess:h=>replaceFont(h,/<i>&#x([a-f0-9]{4});?<\/i>/g)})};