exports=async function(url,bid,cid,options){const{parseContentPaginate,simpleMerger,getLinkElement}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page}.html`),"div.content",{clientOptions:{...(options&&options.clientOptions)},hasNextFn:html=>null!=/\/read\/\d+\/\d+_\d+\.html/.exec(getLinkElement(html,"id=\"next_url\"")),merger:simpleMerger})};