exports=async function(url,bid,cid,options){const{parseContent}=await require("transcode/parse_content");return parseContent(url,"div#content",{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"},transformer:f=>f.replace(/手机站[^<]+地址：[^<]+/,"").replace(/[^<>]+阅读网址：[^<]+感謝支持[^<]+/,"")})};