exports=async function(url,bid,cid,options){const{parseContentPaginate}=await require("transcode/parse_content");return parseContentPaginate(page=>`https://www.xs98.com/xs98${bid}/${cid}${1===page?"":"_"+page}.html`,"div#content",{clientOptions:{...(options&&options.clientOptions)},hasNextFn:html=>null!=/<a href="[^"]*\d+_\d+\.html">下一页</.exec(html)})};