exports=async function(url,bid,cid,options){const{parseContentPaginate,getNextUrlElement}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page}.html`),"div#content",{clientOptions:{...(options&&options.clientOptions)},hasNextFn:html=>/\/\d+_\d+\.html/.test(getNextUrlElement(html)),transformer:f=>f.replace(/<p>[^<]+全文无错[^<]+可以求书一本！[^<]*<\/p>/,"")})};