exports=async function(bid,options){const{parseCatalog}=await require("transcode/parse_catalog"),replaceFont=await require("transcode/font_map");return parseCatalog(`https://www.yanyunxiaoshuo.com/xs/${bid}/`,"div#list>dl>dd",{clientOptions:{...(options&&options.clientOptions)},preprocess:html=>replaceFont(html,/<i>&#x([a-f\d]{4})<\/i>/g)})};