exports=async function(bid,options){const{parseUpdate}=await require("transcode/parse_update"),replaceFont=await require("transcode/font_map");return parseUpdate(`https://m.yanyunxiaoshuo.com/xs/${bid}/`,"pa",{clientOptions:{...(options&&options.clientOptions)},preprocess:html=>replaceFont(html,/<i>&#x([a-f\d]{4})<\/i>/g)})};