exports=async function(url,bid,cid,options){const{parseContent}=await require("transcode/parse_content");let buf;return parseContent(url,"div#content",{clientOptions:{...(options&&options.clientOptions)},preprocess:async h=>{const bufurl=h.subRegex(/var bufurl\s*=\s*['"]([^'"]+)['"]/);if(bufurl){const absoluteUrl=require("utils/absolute_url");buf=await getHtml({url:absoluteUrl(bufurl,url),headers:{"X-Requested-With":"XMLHttpRequest",Referer:url}},options?.clientOptions);}return h},transformer:f=>f+(buf||"")})};