exports=async function(bid,options){const{parseCatalog,cleanCatalogWrapper,hrefRecordRegex}=await require("transcode/parse_catalog"),url=`http://www.yishisc.com/${bid.idPrefix()}/${bid}/`;return parseCatalog(url,{cleaner:html=>cleanCatalogWrapper(html,/class="article_texttable"/,/<\/div>\s*<\/div>/),recordRegex:hrefRecordRegex},{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"}})};