exports=async function(url,bid,cid,options){const{parseContentPaginate,getXiaYiYeElement}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page}.html`),"div#booktxt",{clientOptions:{...(options&&options.clientOptions)},hasNextFn:html=>/\/\d+_\d+\.html/.test(getXiaYiYeElement(html))})};