exports=async function(url,bid,cid,options){const{parseContentPaginate}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".shtml",`_${page}.shtml`),"#chaptercontent",{clientOptions:{...(options&&options.clientOptions),encoding:"gbk","x-mobile":1},hasNextFn:html=>null!=/id="pb_next" href="\/\d+\/\d+\/\d+_\d+\.shtml"/.exec(html),transformer:f=>f.replace(/<p class=[^>]+>[^<]+继续阅读[^<]*<\/p>/,"")})};