exports=async function(bid,options){const{parseInfo}=await require("transcode/parse_info"),replaceFont=await require("transcode/font_map");return parseInfo("\u8BED\u5170\u9601",`https://www.yulange.com/${bid}/`,"meta",{clientOptions:{...(options&&options.clientOptions),"x-mobile":0},preprocess:html=>replaceFont(html,/<i>&#x([a-f\d]{4})<\/i>/g)})};