exports=async function(bid,options){const{parseCatalogPaginate,cleanCatalogWrapper,hrefWrappedRecordRegex,getSelectLastOption}=await require("transcode/parse_catalog"),clientOptions={...(options&&options.clientOptions)};return parseCatalogPaginate(page=>`https://www.yunxiaoge.net/indexlist/${bid}/${1===page?"":page+"/"}`,{cleaner:html=>cleanCatalogWrapper(html,/id="list"/,"</dl>"),recordRegex:hrefWrappedRecordRegex},{...options,clientOptions,pageSize:50,totalPagesRegex:html=>getSelectLastOption(html,s=>-1!==s.indexOf("\u7AE0"))?.subRegex(/\/(\d+)\/"/)?.intValue()})};