exports=async function(url,bid,cid,options){const{parseContentPaginate,hasMorePageCleaner}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page}.html`),"div#booktxt",{clientOptions:{...(options&&options.clientOptions)},hasNextFn:html=>null!=/\/book\/\d+\/\d+_\d+\.html"[^>]*>\s*下一/.exec(html),pageCleaner:hasMorePageCleaner})};