exports=async function(url,bid,cid,options){const{parseContentPaginate,trimContentFragment,getXiaYiZhangElement}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page-1}.html`),{cleaner:html=>trimContentFragment(html,/class="word_read"[^>]*>/)},{clientOptions:{...(options&&options.clientOptions)},hasNextFn:html=>null!=/_\d+[\/_]\d+_\d+\.html/.exec(getXiaYiZhangElement(html))})};