exports=async function(bid,options){const{parseCatalogPaginate,cleanCatalogWrapper,getSelectLastOption,hrefWrappedRecordRegex}=await require("transcode/parse_catalog"),replaceFont=await require("transcode/font_map"),clientOptions={...(options&&options.clientOptions)};return parseCatalogPaginate(page=>`https://www.zianshu.com/${bid}/${page}/`,{cleaner:html=>cleanCatalogWrapper(html,/id="list"/,"</dl>").subIndexOf("\u6B63\u6587</dt>"),recordRegex:hrefWrappedRecordRegex},{...options,clientOptions,pageSize:20,totalPagesRegex:html=>getSelectLastOption(html,s=>-1!==s.indexOf("\u7AE0"))?.subRegex(/\/(\d+)\/"/)?.intValue(),preprocess:html=>replaceFont(html,/<i>&#x([a-f\d]{4})<\/i>/g),idRegex:/\/(?<id>[a-z\d]+)\.html/})};