exports=async function(bid,options){const{send}=require("http"),response=await send(`https://m.dmzj.com/info/${bid}.html`,{...(options&&options.clientOptions),encoding:"utf-8","x-mobile":0});assertOK(response);let html=response.body;return formatCatalog(html.subRegexAll(/,"data":(\[\{"id"[^\]]+\])/g).reverse().map(__=>JSON.parse(__).map(_=>({id:_.id+"",name:_.chapter_name,url:`https://m.dmzj.com/view/${_.comic_id}/${_.id}.html`})).reverse()).flat())};