exports = async function (videoUrl, options) {
  const {
    send
  } = require('http');

  const url = `https://json.vipjx.cnow.eu.org/?url=${videoUrl}`;
  let response = await send(url);
  assertOK(response);
  let html = response.body;
  const result = JSON.parse(html).url;

  if (result == null) {
    return null;
  }

  return {
    url: result
  };
};