exports=async function(bid,options){const url=`${options.recognizeExtra?options.recognizeExtra:"https://www.555pian.com"}/voddetail/${bid}.html`,clientOptions={...(options&&options.clientOptions),encoding:"utf-8"},{send}=require("http");let response=await send(url,clientOptions);assertOK(response);let html=response.body;const fragment=/(?<fragment>(?:<div class="module-list[^>]+>\s*<div[^>]*>\s*<div[^>]*>(?:\s*<a[^>]*>\s*<span>[^<]*<\/span>\s*<\/a>)*\s*<\/div>\s*<\/div>\s*<\/div>\s*)+)/.exec(html).groups.fragment,matches=fragment.matchAll(/<div[^>]+>\s*<a[^>]* href="\/vodplay\/\d+-(?<route>\d+)-\d+\.html"[^>]*>\s*<span>(?<name>[^<]+)/g);let result=[];for(let m of matches)result.push({name:m.groups.name,route:m.groups.route});return formatVideoUpdate({url,updates:result})};