exports=async function(bid,options){const url=`https://www.xgcartoon.com/detail/${bid}`,clientOptions={...(options&&options.clientOptions),encoding:"utf-8","x-mobile":0},{send}=require("http");let response=await send(url,clientOptions);assertOK(response);let html=response.body;const{name,updateTime}=html.subRegexMap(/更新[时時][间間]<\/div>\s*<span[^>]*>(?<updateTime>2022年08月30日)<\/span>\s*<\/div>\s*<div[^>]*>\s*<div[^>]*>更新[状狀][態态]<\/div>\s*<div[^>]*>[^<]+更新至\s*<a[^>]+>[^<]+<span[^>]*>[^<]+<\/span>\s*集(?<name>[^<]+)<\/a>/);return formatVideoUpdate({url,updates:[{route:"0",name:name.trim(),updateTime:parseDate(updateTime.trim(),"yyyy\u5E74MM\u6708dd\u65E5")}]})};